// circuits/credential_issuance/src/main.nr
// Proves: "I am an authorized issuer and this credential is valid"

use dep::std;
use std::hash::pedersen_hash;

fn main(
    // ============================================
    // PRIVATE INPUTS
    // ============================================

    // Issuer's authority proof
    issuer_secret: Field,
    issuer_id: Field,

    // Credential details
    recipient_id: Field,
    credential_type: Field,    // "employment", "education", "skill"
    credential_data: [Field; 8], // Flexible data array
    issue_timestamp: Field,
    expiry_timestamp: Field,

    // Additional metadata
    issuer_name_hash: Field,
    credential_metadata_hash: Field,

    // ============================================
    // PUBLIC INPUTS
    // ============================================

    authorized_issuer_root: pub Field, // Merkle root of authorized issuers
    issuer_merkle_proof: pub [Field; 8],
    issuer_merkle_indices: pub [Field; 8],

    // Validation parameters
    min_validity_period: pub Field, // Minimum days credential is valid
    current_timestamp: pub Field

) -> pub (Field, Field, Field) {

    // ============================================
    // 1. VERIFY ISSUER IS AUTHORIZED
    // ============================================

    // Create issuer leaf
    let issuer_leaf = pedersen_hash([
        issuer_id,
        issuer_secret,
        issuer_name_hash
    ]);

    // Verify issuer is in authorized list
    let computed_root = verify_merkle_proof(
        issuer_leaf,
        issuer_merkle_proof,
        issuer_merkle_indices
    );

    assert(computed_root == authorized_issuer_root, "Unauthorized issuer");

    // ============================================
    // 2. VALIDATE CREDENTIAL DATA
    // ============================================

    // Verify timestamps are logical
    assert(issue_timestamp as u64 < expiry_timestamp as u64, "Invalid expiry");
    assert(issue_timestamp as u64 <= current_timestamp as u64, "Future issue date");

    // Verify minimum validity period
    let validity_period = expiry_timestamp - issue_timestamp;
    assert(validity_period as u64 >= min_validity_period as u64, "Validity too short");

    // Verify credential type is valid (1-10 for different types)
    assert(credential_type as u64 >= 1, "Invalid credential type");
    assert(credential_type as u64 <= 10, "Invalid credential type");

    // ============================================
    // 3. GENERATE OUTPUTS
    // ============================================

    // Credential ID (unique identifier)
    let credential_id = pedersen_hash([
        issuer_id,
        recipient_id,
        credential_type,
        issue_timestamp,
        expiry_timestamp,
        credential_data[0],
        credential_data[1],
        credential_data[2],
        credential_data[3],
        credential_metadata_hash
    ]);

    // Credential commitment (for privacy)
    let credential_commitment = pedersen_hash([
        credential_id,
        recipient_id,
        credential_data[0],
        credential_data[1],
        credential_data[2],
        credential_data[3],
        credential_data[4],
        credential_data[5]
    ]);

    // Issuer commitment (proves which issuer created this)
    let issuer_commitment = pedersen_hash([
        issuer_id,
        credential_id
    ]);

    (credential_id, credential_commitment, issuer_commitment)
}

fn verify_merkle_proof(
    leaf: Field,
    proof: [Field; 8],
    indices: [Field; 8]
) -> Field {
    let mut current = leaf;

    for i in 0..8 {
        let sibling = proof[i];
        let is_right = indices[i];

        if is_right == 1 {
            current = pedersen_hash([sibling, current]);
        } else {
            current = pedersen_hash([current, sibling]);
        }
    }

    current
}

